export interface Record {
    id: string | number;
    [name: string]: any;
}
export interface FieldDescription {
    type: string;
    options?: any;
}
export interface QueryOptions {
    where?(r: Record): boolean;
    preload?: string[];
    limit?: number;
}
export default class MemoryDB {
    private tables;
    private refs;
    constructor();
    createTable(name: string, fieldDescr: {
        [n: string]: FieldDescription;
    }): void;
    insert(name: string, data: Record): string | number;
    query(tableName: string, options?: QueryOptions): Record[];
    get(tableName: string, id: string | number): Record | void;
    update(tableName: string, id: string | number, changes: {
        [key: string]: any;
    }): void;
    delete(table: string, id: string | number): void;
}
